  display: block;
}

.wrapper-contact {
  display: none;
}

.wrapper-contact.open {
  display: block;
}

.home {
  position: fixed;
  bottom: 40px;
  right: 40px;
  background-color: #373737;
  border-radius: 50%;
  background-repeat: no-repeat;
  background-position: center;
