@media (max-width: 1200px) {
  .header__logo {
    display: none;
  }
  .header__logo-adaptive {
    display: block;
  }
  .header__logo-adaptive img {
    margin-top: 30px;
    margin-right: 15px;
  }

  .title__head {
    font-size: 60px;
  }
  .title__subtitle {
    font-size: 20px;
  }

  .slider__carousel {
    height: 530px;
  }
  .slider__slide {
    height: 440px;
  }

  .about-info__col {
    margin: 0 auto;
    margin-bottom: 50px;
  }
  .about-info__col-img {
