    text-align: center;
  }

  .about-info__col-title {
    font-size: 30px;
    margin-bottom: 20px;
  }
  .about-info__col-subtitle {
    font-size: 16px;
    padding-bottom: 15px;
  }

  .blog-items__item {
    flex-wrap: wrap;
  }
  .blog-items__item-img {
    margin: 0 auto;
    margin-bottom: 20px;
  }
  .blog-items__item-title {
    text-align: center;
  }
  .blog-items__item-text {
    margin-bottom: 70px;
  }
  .blog-items__item-user {
    padding-right: 50px;
    margin-bottom: 8px;
  }
  .blog-items__item-box {
    padding: 0 50px;
  }
  .blog-items__item-subtitle {
    flex-wrap: wrap;
  }
  .blog-items__item-subtitle::before {
    left: 50%;
    transform: translateX(-50%);
  }
}
@media (max-width: 950px) {
  .slider__carousel {
    height: 430px;
  }
  .slider__slide {
    height: 340px;
  }
}
@media (max-width: 850px) {
  .contact-form input::placeholder {
    font-size: 18px;
  }
  .contact-form textarea::placeholder {
    font-size: 18px;
  }

  .slider__container {
    padding: 0 50px;
  }
  .slider__carousel {
    height: 350px;
  }
  .slider__carousel-subtitle {
    font-size: 18px;
  }
  .slider__slide {
    height: 290px;
  }

  .retro__title-default {
    display: none;
  }
  .retro__title-adaptive.med {
    display: block;
  }
  .retro__ribbon {
    display: none;
  }
  .retro__ribbon-adaptive.med {
    display: block;
  }
  .retro__subtitle-text {
    font-size: 18px;
  }

  .list-1 {
    margin-left: 20px;
  }

  .list-2 {
    margin-left: 40px;
  }

  .header__menu {
    justify-content: flex-start;
    text-align: center;
    padding-top: 40px;
    max-width: 650px;
    margin: 0 auto;
  }
  .header__menu li + li {
    margin-left: 40px;
  }
}
@media (max-width: 670px) {
  .header__menu {
    display: none;
  }

  .header__bars {
    padding-top: 25px;
    display: flex;
  }
  .header__bars-icon {
    display: block;
    width: 40px;
    background-color: #7ec1c1;
    z-index: 100;
    transition: all 0.3s;
    margin-top: 10px;
  }
  .header__bars-icon.open .bar-once {
    opacity: 0;
  }
  .header__bars-icon.open .bar-1 {
    transition: all 0.3s;
    transform: rotate(45deg) translate(40%, -50%);
  }
  .header__bars-icon.open .bar-3 {
    transition: all 0.3s;
    transform: rotate(-45deg) translate(45%, -50%);
  }
  .header__bars .bar {
    width: 40px;
    margin-bottom: 7px;
    background-color: #f6edd8;
    height: 5px;
    border-radius: 2px;
  }
  .header__bars .bar-1 {
    transition: all 0.3s;
    transform: rotate(0deg) translate(0, 0);
  }
  .header__bars .bar-3 {
    transition: all 0.3s;
    transform: rotate(0deg) translate(0, 0);
  }
  .header__bars .bar-once {
    transition: all 0.3s;
    opacity: 1;
  }
  .header__bars-menu {
    max-width: 600px;
    width: 100%;
    transform: translateX(-120%);
    transition: all 0.5s;
    opacity: 0;
  }
  .header__bars-menu.open {
    transform: translateX(20%);
    transition: all 0.5s;
    opacity: 1;
  }
  .header__bars-menu__item {
    display: flex;
    margin-bottom: 10px;
  }
  .header__bars-menu__item a + a {
    margin-left: 60px;
  }
  .header__bars-menu__item a {
    position: relative;
    padding-left: 15px;
  }
  .header__bars-menu__item a::before {
    content: "";
    position: absolute;
    background-image: url("/0bea80a22920fa97d27d2c09be44ce9b.svg");
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    height: 12px;
    width: 7px;
    left: 0;
    top: 50%;
    transform: translateY(-50%);
  }
  .header__bars-menu__link {
    font-size: 20px;
    color: #f6edd8;
  }

  .retro__subtitle-text {
    font-size: 18px;
  }

  .blog-items__item-title {
    font-size: 36px;
  }
}
@media (max-width: 600px) {
  .slider__carousel {
    height: 280px;
  }
  .slider__slide {
    height: 220px;
  }
}
@media (max-width: 550px) {
  .title {
    margin-top: 30px;
  }
  .title__head {
    font-size: 40px;
    line-height: 70px;
  }
  .title__subtitle {
    font-size: 15px;
  }
}
@media (max-width: 380px) {
  .header__bars-menu.open {
    transform: translateX(10%);
  }
}

sources.scss/style.scss:
@import url('https://fonts.googleapis.com/css2?family=Balsamiq+Sans:wght@400;700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Roboto+Slab&display=swap');

@import "normalize";
@import "mixins";
@import "variables";
@import "slick";

*,
*::after,
*::before{
  box-sizing: border-box;
}

html{
  box-sizing: inherit;
}

body {
  font-family: 'Balsamiq Sans', 'Sans-Serif', cursive;
  font-weight: 500;
  zoom: .75;
  color: #38353e;
}

.wrapper {
  overflow: hidden;
}

a {
  @include linkClear;
}

ul, li {
  margin: 0;
  padding: 0;
  list-style: none;
}

p {
  margin: 0;
  padding: 0;
}

h1, h2, h3, h4, h5, h6 {
  margin: 0;
}

textarea {
  resize: none;
}

.container{
  max-width: 1170px;
  padding: 0 15px;
  margin: 0 auto;
}
.container-items{
  max-width: 1320px;
  padding: 0 15px;
  margin: 0 auto;
}

.container-form {
  max-width: 660px;
  padding: 0 15px;
  margin: 0 auto;
}

.header {
  background-color: #7ec1c1;
  height: 160px;
  box-shadow: 2px 2px 5px rgba(#000, .2);
  &__bars {
    display: none;
  }

  & a {
    font-size: $title-fz;
    color: #f9ebd9;
  }
  
  &__logo-adaptive {
    display: none;
  }
  .list-1{
    display: flex;
  }
  .list-2 {
    display: flex;
  }
  
  &__menu {
    padding-top: 50px;
    display: flex;
    align-items: center;
    position: relative;
    justify-content: space-between;
    margin: 0 auto;

    &-list {
      padding: 0;
    }

    & li + li {
      margin-left: 80px;
    }
  }

  img{
    position: absolute;
    top: 0;
    right: 50%;
    transform: translateX(50%);
  }
}

.retro {
  margin-bottom: 65px;
  margin-top: 65px;

  &__inner {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  }

  &__title{
    margin-bottom: 40px;
  }
  
  &__title-adaptive {
    display: none;
  }
  
  &__ribbon-adaptive {
    display: none;
  }

  &__subtitle {
    position: relative;

    &-text {
      position: absolute;
      top: 50%;
      left: 50%;
      transform: translate(-50%, -65%);
      color: #4b4e4c;
      font-size: $title-fz;
    }
  }
}

.slider {
  padding-bottom: 100px;
  &__container {
    max-width: 1320px;
    padding: 0 100px;
    margin: 0 auto;
  }
  
  &__carousel {
    box-shadow: 2px 2px 3px rgba(#000, .3);
    height: 630px;
    background-color: #fbfaf6;
    padding-left: 15px;
    padding-top: 15px;
    border-radius: 10px;
    margin-bottom: 45px;

    &-subtitle {
      font-size: $title-fz;
      font-family: 'Times New Roman';
      color: #383a3c;
      text-align: center;
      margin-top: 10px;
    }
  }

  &__slide {
    height: 540px;
    background-color: #e3dfd7;
    margin: 0 20px;
  }

  &__subtitle {
    font-size: 42px;
    color: #f7ebd8;
    text-align: center;
    display: block;
    &-adaptive {
      display: none;
    }
  }

  .slick {
    &-prev{
      outline: none;
      cursor: pointer;
      font-size: 0;
      line-height: 0;
      padding: 0;

      border: none;
      width: 60px;
      height: 80px;
      background-color: #fdf9f6;
      position: absolute;
      z-index: 1;
      border-radius: 50%;
      border-bottom-left-radius: 0px;
      border-top-left-radius: 0px;
      top: 50%;
      left: -15px;
      transform: translateY(-50%);

      &::before {
        content: "";
        background-image: url('../images/arrow-left.svg');
        position: absolute;
        width: 25px;
        height: 30px;
        top: 50%;
        right: 25%;
        transform: translateY(-50%);
      }
    }

    &-next {
      outline: none;
      font-size: 0;
      line-height: 0;
      padding: 0;
      cursor: pointer;

      border: none;
      width: 60px;
      height: 80px;
      background-color: #fdf9f6;
      position: absolute;
      z-index: 1;
      border-radius: 50%;
      border-bottom-right-radius: 0px;
      border-top-right-radius: 0px;
      top: 50%;
      right: 0;
      transform: translateY(-50%);

      &::before {
        content: "";
        background-image: url('../images/arrow-right.svg');
        position: absolute;
        width: 25px;
        height: 30px;
        top: 50%;
        right: 25%;
        transform: translateY(-50%);
      }
    }
  }
}
// page 1 ended

// page 2
.title.contact {
  color: #f6edd8;
  .title__times-time {
    background-image: url('../images/times-contact.svg');
  }
  .title__times-border {
    background-color: #f6edd8;
  }
}
.title {
  color: #38353e;
  text-align: center;
  padding-bottom: 40px;
  margin-top: 65px;
  &__head {
    text-transform: uppercase;
    font-size: 100px;
    font-weight: 700;
    line-height: 90px;
  }
  &__times {
    z-index: 100;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-bottom: 15px;
    &-border {
      width: 55px;
      height: 2px;
      background-color: #38353e;
    }
    &-time {
      background-image: url('../images/times.svg');
      width: 14px;
      height: 14px;
      background-size: cover;
      background-position: center;
      background-repeat: no-repeat;
    }
    &-wrapper {
      display: flex;
      justify-content: space-between;
      width: 45px;
      margin: 0 12px;
    }
  }
  &__subtitle {
    text-transform: uppercase;
    font-size: 22px;
    font-weight: 400;
  }
}
.about {
  &-info {
    padding-bottom: 100px;
    &__inner {
      display: flex;
      justify-content: space-between;
      width: 100%;
      flex-wrap: wrap;
    }
    &__col {
      max-width: 250px;
      width: 100%;
      font-weight: 400;
      padding: 20px;
      padding-top: 0;
      &-title {
        text-align: center;
        font-size: 37px;
        font-weight: 700;
        margin-bottom: 30px;
        text-transform: uppercase;
      }
      &-subtitle {
        text-align: center;
        text-transform: uppercase;
        padding-bottom: 25px;
        margin-bottom: 20px;
        position: relative;
        font-size: 20px;
        &::before {
          @include bdr;
        }
      }
      &-text {
        span {
          text-decoration: underline;
        }
      }
      &-img {
        width: 190px;
        height: 190px;
        border-radius: 50%;
        border: 5px solid  #d45244;
        margin: 0 auto;
        margin-bottom: 25px;
        .col-img__inner {
          border: 5px solid  #fff;
          background-color: #d45244;
          width: 180px;
          height: 180px;
          border-radius: 50%;
          position: relative;
        }
      }
    }
  }
}
.col-img__image {
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
  width: 100px;
  height: 100px;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  &.movies {
    background-image: url('../images/movies-icon.svg');
  }
  &.music{
    background-image: url('../images/music-icon.svg');
  }
  &.clothes {
    background-image: url('../images/clothes-icon.svg');
  }
  &.food {
    background-image: url('../images/food-icon.svg');
  }
}
// ended page 2 

// page 3
.portfolio {
  &-items {
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
  }
  &-filter {
    margin-bottom: 25px;
    span {
      margin-right: 30px;
    }
    button {
      border: none;
      background-color: #fff;
      font-size: 16px;
      font-weight: 700;
      padding: 6px 10px;
      padding-left: 18px;
      position: relative;
      box-shadow: 2px 2px 10px rgba(0, 0, 0, 0.03);
      border-radius: 5px;
      border-bottom-left-radius: 0;
      border-top-left-radius: 0;
      cursor: pointer;
      outline: none;
      & + button {
        margin-left: 25px;
      }
      &::after {
        content: "";
        position: absolute;
        background-color: #959ca3;
        width: 5px;
        height: 5px;
        top: 50%;
        left: 0;
        transform: translateY(-50%);
        border-radius: 50%;
      }
      &::before {
        content: "";
        position: absolute;
        border: 16px solid transparent; 
        border-right: 16px solid #fff;
        left: -32px;
        top: 0;
        z-index: 5;
      }
    }
    &__btn {
      text-align: center;
      padding-bottom: 80px;
    }
    &__all {
      margin-top: 45px;
      padding-top: 15px;
      position: relative;
      background-color: transparent;
      border: none;
      cursor: pointer;
      font-size: 19px;
      font-weight: 700;
      text-align: center;
      text-transform: uppercase;
      outline: none;
      &::before {
        content: "";
        position: absolute;
        width: 55px;
        height: 3px;
        background-color: #000;
        top: 0;
        left: 50%;
        transform: translateX(-50%);
      }
    }
  }
  &-item {
    width: 300px;
    padding: 5px;
    padding-bottom: 20px;
    background-color: #fff;
    margin-bottom: 25px;
    border-radius: 5px;
    box-shadow: 3px 3px 5px rgba(0, 0, 0, .2);
    position: relative;
    &__img {
      width: 290px;
      height: 220px;
      background-size: cover;
      background-repeat: no-repeat;
      background-position: center;
    }
    
    &__icon {
      background-color: #fff;
      border-radius: 50%;
      transform: translate(-25px, -50%);
      width: 55px;
      height: 50px;
      left: 50%;
      position: absolute;
      img {
        width: 30px;
        margin-top: 15px;
        margin-left: 12px;
      }
    }
    
    &__name {
      text-transform: uppercase;
      text-align: center;
      font-weight: 700;
      padding-top: 30px;
    }
  }
}

.blog {
  &-items {
    &__button {
      width: 100%;
      text-align: center;
      padding-bottom: 100px;
      &-link {
        margin-top: 45px;
        padding-top: 15px;
        position: relative;
        background-color: transparent;
        border: none;
        cursor: pointer;
        font-size: 19px;
        font-weight: 700;
        text-transform: uppercase;
        outline: none;
        &::before {
          content: "";
          position: absolute;
          width: 55px;
          height: 3px;
          background-color: #000;
          top: 0;
          left: 50%;
          transform: translateX(-50%);
        }
      }
    }
    &__item {
      margin-bottom: 45px;
      display: flex;
      align-items: flex-start;
      &-img {
        box-shadow: 3px 3px 5px rgba(0, 0, 0, .2);
        padding: 5px;
        margin-right: 30px;
        background-color: #fff;
        border-radius: 5px;
        &__image {
          background-repeat: no-repeat;
          background-size: cover;
          background-position: center;
          width: 400px;
          height: 300px;
        }
      }
      &-title {
        font-size: 45px;
        font-weight: 700;
        margin-bottom: 10px;
      }
      &-subtitle {
        text-transform: uppercase;
        font-weight: 700;
        font-size: 16px;
        display: flex;
        width: 100%;
        justify-content: space-between;
        padding-bottom: 20px;
        margin-bottom: 20px;
        position: relative;
        &::before {
          @include bdr;
          left: 0;
          transform: translate(0, 0);
        }
      }
      &-box {
        max-width: 850px;
      }
      &-text {
        font-size: 20px;
        line-height: 30px;
        font-weight: 400;
        font-family: 'Roboto Slab', serif;
      }
    }
  }
}

.contact {
  &__button {
    width: 100%;
    text-align: center;
    margin-top: 55px;
    &-link {
      padding-bottom: 15px;
      margin-bottom: 20px;
      position: relative;
      background-color: transparent;
      border: none;
      cursor: pointer;
      font-size: 19px;
      font-weight: 700;
      text-transform: uppercase;
      color: #f6edd8;
      outline: none;
      &::before {
        content: "";
        position: absolute;
        width: 60px;
        height: 3px;
        background-color: #f6edd8;
        bottom: 0;
        left: 50%;
        transform: translateX(-50%);
      }
      span {
        margin-left: 25px;
      }
      span::before {
        content: "";
        background-image: url('../images/sqrt.svg');
        position: absolute;
        width: 30px;
        height: 30px;
        left: 37px;
        top: -7px;
      }
      span::after {
        content: "";
        position: absolute;
        width: 70px;
        height: 1.3px;
        background-color: #f6edd8;
        left: 64px;
        top: -7px;
      }
      span.s::after {
        content: "";
        position: absolute;
        width: 125px;
        height: 1.3px;
        background-color: #f6edd8;
        left: 64px;
        top: -7px;
      }
    }
  }
  &-form {
    padding: 0 50px;
    padding-bottom: 100px;
    form.error {
      input {
        border: 4px solid #ed0000;
        border-radius: 10px;
      }
      textarea {
        border: 4px solid #ed0000;
        border-radius: 10px;
      }
    }
    input {
      width: 100%;
      border: none;
      outline: none;
      margin-bottom: 15px;
      border-radius: 5px;
      box-shadow: 3px 3px 5px rgba(0, 0, 0, .2);
      height: 50px;
      padding: 15px 10px;
      font-size: 20px;
      font-weight: 700;
      &::placeholder {
        font-size: 22px;
        color: #a9a9a9;
      }
      
      &.false:focus {
        border: 5px solid #ed0000;
        border-radius: 10px;
      }
      &.true:focus {
        border: 5px solid #10a400;
        border-radius: 10px;
      }
    }
    textarea {
      width: 100%;
      border: none;
      padding: 15px 10px;
      outline: none;
      height: 125px;
      font-size: 26px;
      font-weight: 700;
      border-radius: 5px;
      box-shadow: 3px 3px 5px rgba(0, 0, 0, .2);
      &::placeholder {
        font-size: 22px;
        color: #a9a9a9;
      }
    }
    button {
      cursor: pointer;
      font-weight: 700;
      border: none;
      background-color: #fff;
      box-shadow: 3px 3px 5px rgba(0, 0, 0, .2);
      padding: 15px 25px;
      outline: none;
      border-radius: 5px;
      margin-top: 15px;
      font-size: 22px;
    }
  }
  &-social {
    padding-bottom: 100px;
    &__inner {
      display: flex;
      justify-content: space-between;
      margin-top: 50px;
    }
    &__item {
      height: 110px;
      width: 110px;
      border-radius: 50%;
      background-color: #f5eee3;
      display: flex;
      align-items: center;
      justify-content: center;
      &-inner {
        height: 105px;
        border-radius: 50%;
        background-color: #f5eee3;
        width: 105px;
        border: 5px solid #37393b;
        position: relative;
        span {
          position: absolute;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);
          font-size: 42px;
        }
      }
    }
  }
}
.wrapper-main {
  display: none;
}
.wrapper-main.open {
  display: block;
  .home {
    display: none;
  }
}
.wrapper-about {
  display: none;
}
.wrapper-about.open {
  display: block;
}
.wrapper-portfolio {
  display: none;
}
.wrapper-portfolio.open {
  display: block;
}
.wrapper-blog {
  display: none;
}
.wrapper-blog.open {
  display: block;
}
.wrapper-contact {
  display: none;
}
.wrapper-contact.open {
  display: block;
}

.home {
  position: fixed;
  bottom: 40px;
  right: 40px;
  background-color: #373737;
  border-radius: 50%;
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
  padding: 30px;
  z-index: 9999;
  img {
    margin-top: 12px;
    z-index: 9999;
  }
}

